import React, { useState, useEffect } from 'react';
import AddTodo from '../components/AddTodo';
import TodoList from '../components/TodoList';
import { getTodos, addTodo, updateTodo, deleteTodo } from '../services/todoService';

function Dashboard() {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    async function fetchTodos() {
      const data = await getTodos();
      setTodos(data);
    }
    fetchTodos();
  }, []);

  const handleAddTodo = async (newTodo) => {
    const data = await addTodo(newTodo);
    setTodos([...todos, data]);
  };

  const handleUpdateTodo = async (id, updatedTodo) => {
    const data = await updateTodo(id, updatedTodo);
    setTodos(todos.map((todo) => (todo._id === data._id ? data : todo)));
  };

  const handleDeleteTodo = async (id) => {
    await deleteTodo(id);
    setTodos(todos.filter((todo) => todo._id !== id));
  };

  return (
    <div >
      <h1>Dashboard</h1>
      <AddTodo handleAddTodo={handleAddTodo} />
      <TodoList todos={todos} handleUpdateTodo={handleUpdateTodo} handleDeleteTodo={handleDeleteTodo} />
    </div>
  );
}

export default Dashboard;
